package dev.yafatek.restcore.api.utils;/** * High level API Response, this model represents the Generic response for our apis, * it will be a standard cross the system. * * @param <T> Results Parameter content Type, basically a Java Class or any Primitive type. * @author Feras E Alawadi * @version 1.0.1 * @since 1.0.1 */public final class ApiResponse<T, R> {    private final boolean status;    private final String code;    private final String message;    private final T results;    private R errors;    public ApiResponse(boolean status, String code, String message, T results) {        this.status = status;        this.code = code;        this.message = message;        this.results = results;    }    public ApiResponse(boolean status, String code, String message, T results, R errors) {        this.status = status;        this.code = code;        this.message = message;        this.results = results;        this.errors = errors;    }    public R getErrors() {        return errors;    }    public void setErrors(R errors) {        this.errors = errors;    }    public boolean isStatus() {        return status;    }    public String getCode() {        return code;    }    public String getMessage() {        return message;    }    public T getResults() {        return results;    }}